    <div className="componentsBoxLayout">
      <p>{resultData.name} | {resultData.sys.country} {dayInAWeek}</p>
      <p>{resultData.weather[0].description}</p>
      <p><img alt="Weather Icon" src={`icons/${resultData.weather[0].icon}.png`} /></p>
