import React, { useState } from "react";
// import { Api } from '../../Api';

function Search({ textPlaceholder, searchResultFunction }) {
  const [searchCity, setSearchCity] = useState("");
  // const [weatherData, setWeatherData] = useState({});

  {/*} const searchLocation = (event) => {
    if (event.key === "Enter") {
      fetch(`${Api.url}/weather/?q=${searchCity}&units=metric&appid=${Api.key}`)
        .then((response) => response.json())
        .then((result) => {
          setSearchCity("");
          setWeatherData(result);
          searchResultFunction(result);
        });
    }
  }; */}

  const handleOnChange = (searchData) => {
    setSearchCity((event) => setSearchCity(event.target.value));
    setSearchCity(searchData);
    searchResultFunction(searchData);
  }

  return (
    <div className="componentsBoxLayout">
      <input
        value={searchCity}
        onChange={handleOnChange}
        onKeyPress={searchResultFunction}
        type="text"
        placeholder={textPlaceholder}
      />
      {/* {typeof weatherData.main != "undefined" ? (
        <div>
          <p>{weatherData.name}</p>
        </div>
      ) : (
        ""
      )} */}
    </div>
  );
}

export default Search;
