import React, { useState } from "react";

function Search({ textPlaceholder, searchResultFunction }) {
  const [searchCity, setSearchCity] = useState("");
  const [weatherData, setWeatherData] = useState({});

  const api = {
    url: "https://api.openweathermap.org/data/2.5",
    key: "fc8975bd4701139dcd78e2e288cc3807",
    img: "https://openweathermap.org/img/wn",
  };

  const searchLocation = (event) => {
    if (event.key === "Enter") {
      fetch(`${api.url}/weather/?q=${searchCity}&units=metric&appid=${api.key}`)
        .then((response) => response.json())
        .then((result) => {
          setSearchCity("");
          setWeatherData(result);
          searchResultFunction(result);
        });
    }
  };

  return (
    <div className="componentsBoxLayout">
      <input
        value={searchCity}
        onChange={(event) => setSearchCity(event.target.value)}
        onKeyPress={searchLocation}
        type="text"
        placeholder={textPlaceholder}
      />
      {searchResultFunction(typeof weatherData.main != "undefined" ? (
        <div>
          <p>{weatherData.name}</p>
        </div>
      ) : (
        ""
      ))
      
      }
    </div>
  );
}

export default Search;
