  const searchLocation = () => {
    fetch(`${Api.url}/weather/?q=${searchCity}&units=metric&appid=${Api.key}`)
      .then((response) => response.json())
      .then((result) => {
        setSearchCity(result);
        //setWeatherData(result);
        searchResultData(result);
      });
  };

  return (
    <div className="App">
      <Search
