import React, { useState } from 'react';
import './App.css';
import { Search, CurrentWeather, ForecastWeather, Footer } from './components/index';


function App() {

  const [searchResult, setSearchResult] = useState();

  const getSearchResult = (result) => {
    setSearchResult(result);
  };

  return (
    <div className="App">
      <Search textPlaceholder="Search for a place..." searchResultFunction={getSearchResult} />
      <CurrentWeather searchResultData={searchResult} />
      <ForecastWeather />
      <Footer />
    </div>
  );
}

export default App;
