import React from "react";

const weekDay = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];

function CurrentWeather({ resultData }) {

  const day = new Date();
  const dayOfWeek = weekDay[day.getDay()];
  
  return (
    <div className="componentsBoxLayout">
    <p><img alt="Weather Icon" src={`icons/${resultData.weather[0].icon}.png`} /></p>
    <p>{Math.round(resultData.main.temp)}Â°C</p>
    <p>{resultData.weather[0].description}</p>
    
    <p>Humidity {resultData.main.humidity}%</p>
    <p>{resultData.wind.speed} mph</p>
      <p>{resultData.name} | {resultData.sys.country} {dayOfWeek}</p>
      
      
           
    </div>
  );
}

export default CurrentWeather;
