import React, { useState } from "react";
import { Api } from "./Api";
import {
  Search,
  CurrentWeather,
  ForecastWeather,
  Footer,
} from "./components/index";
import "./App.css";

function App() {
  //const [getCity, setGetCity] = useState();  
  //const [weatherData, setWeatherData] = useState({});
  //const [forecastData, setForecastData] = useState({});

  const handleSearchLocation = (searchData) => {
    const getCity = searchData.value.splice(" ");
    fetch(`${Api.url}/weather/?q=${getCity}&units=metric&appid=${Api.key}`)
      .then((response) => response.json())
      .then((result) => {
        //setGetCity(result);
        //setWeatherData(result);
      });
  };

  return (
    <div className="App">
      <Search
        searchResultData={handleSearchLocation}
        textPlaceholder="Search for a place..."
      />
      <CurrentWeather
      //searchResultData={weatherData}
      />
      <ForecastWeather />
      <Footer />
    </div>
  );
}

export default App;
