import React, { useState } from "react";
import { Api } from "../../Api";
import {
  Search,
  CurrentWeather,
  ForecastWeather,
  Footer,
} from "./components/index";
import "./App.css";

function App() {
  const [getCity, setGetCity] = useState(null);  
  //const [weatherData, setWeatherData] = useState({});
  //const [forecastData, setForecastData] = useState({});

  const searchLocation = () => {
    fetch(`${Api.url}/weather/?q=${getCity}&units=metric&appid=${Api.key}`)
      .then((response) => response.json())
      .then((result) => {
        setSearchCity(result);
        //setWeatherData(result);
        searchResultData(result);
      });
  };

  return (
    <div className="App">
      <Search
        searchResultData={searchLocation}
        textPlaceholder="Search for a place..."
      />
      <CurrentWeather
      //searchResultData={weatherData}
      />
      <ForecastWeather />
      <Footer />
    </div>
  );
}

export default App;
