import React, { useState } from "react";
import { Api } from "./Api";
import {
  Search,
  CurrentWeather,
  ForecastWeather,
  Footer,
} from "./components/index";
import "./App.css";

function App() {
  //const [getCity, setGetCity] = useState();  
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);

  const handleSearchLocation = (searchData) => {
    const [lat, lon] = searchData.value.splice(" ");

    const weatherDataFetch = fetch(`${Api.url}/weather?lat=${lat}&lon=${lon}&units=metric&appid=${Api.key}`);
    const forecastDataFetch = fetch(`${Api.url}/forecast?lat=${lat}&lon=${lon}&units=metric&appid=${Api.key}`);

    Promise.all([weatherDataFetch, forecastDataFetch])
    .then((response) => response.json())
      .then((result) => {
        //setGetCity(result);
        //setWeatherData(result);
      });
  };

  return (
    <div className="App">
      <Search
        searchResultData={handleSearchLocation}
        textPlaceholder="Search for a place..."
      />
      <CurrentWeather
      //searchResultData={weatherData}
      />
      <ForecastWeather />
      <Footer />
    </div>
  );
}

export default App;
