import React from "react";
import { Row, Col } from "react-bootstrap";

const weekDayName = [
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
  "Sunday",
];

function ForecastWeather({ resultData }) {
  const dayInAWeek = new Date().getDay();
  const forecastWeekDay = weekDayName
    .slice(dayInAWeek, weekDayName.length)
    .concat(weekDayName.slice(0, dayInAWeek));

  console.log(forecastWeekDay);
  console.log(resultData)

  return (
    <Row className="justify-content-center forecastContainer">
      <Col sm={2}>
        {resultData.list.splice(0, 7).map((item, index) => (
          <div key={index}>
            <p>{forecastWeekDay[index]}</p>

            <p>
              <img
                alt="Weather Icon"
                src={`icons/${item.weather[0].icon}.png`}
              />
            </p>

            <p>{item.weather[0].description}</p>
            <p>
              {Math.round(item.main.temp_min)}°{Math.round(item.main.temp_max)}°
            </p>
          </div>
        ))}
      </Col>
    </Row>
  );
}

export default ForecastWeather;
