import React from "react";
import { Row, Col } from 'react-bootstrap';

const weekDay = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wednesday",
  "Thursday",
  "Friday",
  "Saturday",
];

function CurrentWeather({ resultData }) {
  const day = new Date();
  const dayOfWeek = weekDay[day.getDay()];

  return (
    <Row className="justify-content-xs-center weatherBox">      
        <Col md={4} className="currentContainerLeft">
          
            <img
              className="centerImgCurrent"
              alt="Weather Icon"
              src={`icons/${resultData.weather[0].icon}.png`}
            />
            <div className="tempNumber">{Math.round(resultData.main.temp)}</div> Â°C
                    
        </Col>

        <Col md={2} className="currentContainerLeft">
          <p>Humidity: {resultData.main.humidity}%</p>
          <p>Wind: {Math.round(resultData.wind.speed)} m/s</p>
        </Col>

        <Col md={6} className="currentContainerRight">
          <p>
            {resultData.name} - {resultData.sys.country}{" "}
          </p>
          <p>{dayOfWeek}</p>
          <p>{resultData.weather[0].description}</p>
        </Col>
    </Row>
  );
}

export default CurrentWeather;
