import React, { useState } from "react";

function Search({ textPlaceholder, searchResultData }) {
  const [searchCity, setSearchCity] = useState("");

  const handleChange = (searchData) => {
    setSearchCity(searchData, (event) => setSearchCity(event.target.value));
    searchResultData(searchData);
  }

  return (
    <div className="componentsBoxLayout">
      <input
        value={searchCity}
        onChange={handleChange}
        onKeyDown={(event) =>
          event.key === "Enter" && searchResultData(event)
        }
        placeholder={textPlaceholder}
      />
    </div>
  );
}

export default Search;
