import React, { useState } from 'react';
import { Search, CurrentWeather, ForecastWeather, Footer } from './components/index';
import { Api } from './Api';
import './App.css';

function App() {
  const [cityName, setCityName] = useState("");
  const [weatherData, setWeatherData] = useState(null);
  //const [forecastData, setForecastData] = useState({});

  const searchLocation = (searchData) => {    
      fetch(`${Api.url}/weather/?q=${cityName}&units=metric&appid=${Api.key}`)
        .then((response) => response.json())
        .then((result) => {
          setCityName(searchData);
          setWeatherData(result);
        });

  };

  return (
    <div className="App">
      <Search textPlaceholder="Search for a place..." searchResultFunction={searchLocation} />
       {weatherData && <CurrentWeather //searchResultData={searchLocation}
        />}
      <ForecastWeather />
      <Footer />
    </div>
  );
}

export default App;
